mapper.select:
SELECT * 
FROM sys_user a
WHERE id = #{id}

mapper.update:
UPDATE sys_user a SET
	a.`account` = #{account},
	a.`password` = #{password},
	a.isEnable = #{isEnable}
WHERE a.`id` = #{id}

UPDATE sys_user a SET
	a.isEnable = #{isEnable}
WHERE 1 = 1

	AND a.`id` in #{item}

UPDATE sys_user a SET
	a.password = #{password}
WHERE 1 = 1

	AND a.`id` in #{item}

mapper.delete:
DELETE FROM sys_user

	
		WHERE 1 != 1
	
	
		WHERE `id` = #{id}
	
	
		WHERE `id` in #{id}
