-- ----------------------------
--  Table structure for `commodity`
-- ----------------------------
DROP TABLE IF EXISTS `commodity`;
CREATE TABLE `commodity` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '流水号',
  `commodity_name` varchar(100) DEFAULT NULL COMMENT '产品名称',
  `images` varchar(1000) DEFAULT NULL COMMENT '产品图片',
  `bar_code` varchar(255) NOT NULL COMMENT '产品条形码',
  `pro_type_id` int(10) unsigned DEFAULT NULL COMMENT '二级分类ID',
