  `createtime` datetime DEFAULT NULL COMMENT '创建时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

-- ----------------------------
--  Table structure for `customer`
-- ----------------------------
DROP TABLE IF EXISTS `customer`;
CREATE TABLE `customer` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '流水号',
  `createtime` datetime DEFAULT NULL COMMENT '创建时间',
  `modified_time` datetime DEFAULT NULL COMMENT '最后一次更新时间',
  `customer_mode` tinyint(3) unsigned DEFAULT '0' COMMENT '客户模式(0 O2O模式)',
  `customer_type` tinyint(3) unsigned DEFAULT NULL COMMENT '客户类型(0单店 1多店 2线下)',
  `customer_name` varchar(30) DEFAULT NULL COMMENT '客户联系人',
  `customer_phone` varchar(30) DEFAULT NULL COMMENT '客户电话',
  `customer_address` varchar(150) DEFAULT NULL COMMENT '客户详细地址',
  `prov_id` int(10) unsigned DEFAULT NULL COMMENT '省份',
  `city_id` int(10) unsigned DEFAULT NULL COMMENT '城市',
  `area_id` int(10) unsigned DEFAULT NULL COMMENT '区域',
  `open_time` datetime DEFAULT NULL COMMENT '入网时间',
  `end_time` datetime DEFAULT NULL COMMENT '到期时间',
  `isvalid` tinyint(3) unsigned DEFAULT '0' COMMENT '是否有效（0有效 1无效）',
  `status` tinyint(3) unsigned DEFAULT NULL COMMENT '审核状态（0未审核 1同意 2拒绝）',
  `refuse_reason` varchar(150) DEFAULT NULL COMMENT '审核备注',
  `refuse_time` datetime DEFAULT NULL COMMENT '审核时间',
  `agent_id` int(10) unsigned DEFAULT NULL COMMENT '所属代理商ID或者直销人员ID',
  `user_id` int(10) unsigned DEFAULT NULL COMMENT '用户ID',
  `store_num` int(10) unsigned DEFAULT NULL COMMENT '门店数量',
  `account_source` tinyint(3) unsigned DEFAULT NULL COMMENT '账号来源(0代理商 1直销人员)',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8mb4;

